import "../assets/css/galeria.css";
import Heart from "./Heart";
import { useContext } from "react";
import context from "../my_context.js";

export default function Home() {
  const { photos, setPhotos } = useContext(context);

  const handleOnClick = (id) => {
    console.log(id);
    let photosLiked = photos;
    photos.find((element, index) => {
      if (element.id === id) {
        console.log(photosLiked[index].liked);
        if (photosLiked[index].liked) {
          photosLiked[index].liked === true;
        } else {
          photosLiked[index].liked === true;
        }
        console.log(photosLiked[index].liked);
      }
    });
    console.log(photosLiked);
    setPhotos(photosLiked);
  };

  return (
    <div className="galeria grid-columns-5 p-3">
      {photos.map((photo) => (
        <div
          className="foto"
          key={photo.id}
          style={{ backgroundImage: `url(${photo.src.medium})` }}
          onClick={() => {
            handleOnClick(photo.id);
          }}
        >
          <Heart filled={photo.liked} />
          <p>{photo.alt}</p>
        </div>
      ))}
    </div>
  );
}
